mapper.select:
select role.*, ,
from ( select
from sys_role role
limit #{criteria.offset}, #{criteria.size}
) role left join sys_roles_menus srm on role.role_role_id = srm.role_id left join sys_menu menu on menu.menu_id = srm.menu_id left join sys_roles_depts srd on role.role_role_id = srd.role_id left join sys_dept dept on dept.dept_id = srd.dept_id order by role.role_level
